SET FOREIGN_KEY_CHECKS=0;

CREATE DATABASE `wp-ipoint`
  CHARACTER SET `latin1`
  COLLATE `latin1_swedish_ci`;

USE `wp-ipoint`;

/* Tables */
CREATE TABLE `wp_ipointcommentmeta` (
  `meta_id`     bigint UNSIGNED AUTO_INCREMENT NOT NULL,
  `comment_id`  bigint UNSIGNED NOT NULL DEFAULT '0',
  `meta_key`    varchar(255),
  `meta_value`  longtext,
  PRIMARY KEY (`meta_id`)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointcomments` (
  `comment_ID`            bigint UNSIGNED AUTO_INCREMENT NOT NULL,
  `comment_post_ID`       bigint UNSIGNED NOT NULL DEFAULT '0',
  `comment_author`        tinytext NOT NULL,
  `comment_author_email`  varchar(100) NOT NULL,
  `comment_author_url`    varchar(200) NOT NULL,
  `comment_author_IP`     varchar(100) NOT NULL,
  `comment_date`          datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `comment_date_gmt`      datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `comment_content`       text NOT NULL,
  `comment_karma`         int NOT NULL DEFAULT '0',
  `comment_approved`      varchar(20) NOT NULL DEFAULT '1',
  `comment_agent`         varchar(255) NOT NULL,
  `comment_type`          varchar(20) NOT NULL,
  `comment_parent`        bigint UNSIGNED NOT NULL DEFAULT '0',
  `user_id`               bigint UNSIGNED NOT NULL DEFAULT '0',
  PRIMARY KEY (`comment_ID`)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointlinks` (
  `link_id`           bigint UNSIGNED AUTO_INCREMENT NOT NULL,
  `link_url`          varchar(255) NOT NULL,
  `link_name`         varchar(255) NOT NULL,
  `link_image`        varchar(255) NOT NULL,
  `link_target`       varchar(25) NOT NULL,
  `link_description`  varchar(255) NOT NULL,
  `link_visible`      varchar(20) NOT NULL DEFAULT 'Y',
  `link_owner`        bigint UNSIGNED NOT NULL DEFAULT '1',
  `link_rating`       int NOT NULL DEFAULT '0',
  `link_updated`      datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `link_rel`          varchar(255) NOT NULL,
  `link_notes`        mediumtext NOT NULL,
  `link_rss`          varchar(255) NOT NULL,
  PRIMARY KEY (`link_id`)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointoptions` (
  `option_id`     bigint UNSIGNED AUTO_INCREMENT NOT NULL,
  `option_name`   varchar(64) NOT NULL,
  `option_value`  longtext NOT NULL,
  `autoload`      varchar(20) NOT NULL DEFAULT 'yes',
  PRIMARY KEY (`option_id`)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointpostmeta` (
  `meta_id`     bigint UNSIGNED AUTO_INCREMENT NOT NULL,
  `post_id`     bigint UNSIGNED NOT NULL DEFAULT '0',
  `meta_key`    varchar(255),
  `meta_value`  longtext,
  PRIMARY KEY (`meta_id`)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointposts` (
  ID                       bigint UNSIGNED AUTO_INCREMENT NOT NULL,
  `post_author`            bigint UNSIGNED NOT NULL DEFAULT '0',
  `post_date`              datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt`          datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content`           longtext NOT NULL,
  `post_title`             text NOT NULL,
  `post_excerpt`           text NOT NULL,
  `post_status`            varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status`         varchar(20) NOT NULL DEFAULT 'open',
  `ping_status`            varchar(20) NOT NULL DEFAULT 'open',
  `post_password`          varchar(20) NOT NULL,
  `post_name`              varchar(200) NOT NULL,
  `to_ping`                text NOT NULL,
  `pinged`                 text NOT NULL,
  `post_modified`          datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt`      datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered`  longtext NOT NULL,
  `post_parent`            bigint UNSIGNED NOT NULL DEFAULT '0',
  `guid`                   varchar(255) NOT NULL,
  `menu_order`             int NOT NULL DEFAULT '0',
  `post_type`              varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type`         varchar(100) NOT NULL,
  `comment_count`          bigint NOT NULL DEFAULT '0',
  PRIMARY KEY (ID)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointterms` (
  `term_id`     bigint UNSIGNED AUTO_INCREMENT NOT NULL,
  `name`        varchar(200) NOT NULL,
  `slug`        varchar(200) NOT NULL,
  `term_group`  bigint(10) NOT NULL DEFAULT '0',
  PRIMARY KEY (`term_id`)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointterm_relationships` (
  `object_id`         bigint UNSIGNED NOT NULL DEFAULT '0',
  `term_taxonomy_id`  bigint UNSIGNED NOT NULL DEFAULT '0',
  `term_order`        int NOT NULL DEFAULT '0',
  PRIMARY KEY (`object_id`, `term_taxonomy_id`)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointterm_taxonomy` (
  `term_taxonomy_id`  bigint UNSIGNED AUTO_INCREMENT NOT NULL,
  `term_id`           bigint UNSIGNED NOT NULL DEFAULT '0',
  `taxonomy`          varchar(32) NOT NULL,
  `description`       longtext NOT NULL,
  `parent`            bigint UNSIGNED NOT NULL DEFAULT '0',
  `count`             bigint NOT NULL DEFAULT '0',
  PRIMARY KEY (`term_taxonomy_id`)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointusermeta` (
  `umeta_id`    bigint UNSIGNED AUTO_INCREMENT NOT NULL,
  `user_id`     bigint UNSIGNED NOT NULL DEFAULT '0',
  `meta_key`    varchar(255),
  `meta_value`  longtext,
  PRIMARY KEY (`umeta_id`)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointusers` (
  ID                     bigint UNSIGNED AUTO_INCREMENT NOT NULL,
  `user_login`           varchar(60) NOT NULL,
  `user_pass`            varchar(64) NOT NULL,
  `user_nicename`        varchar(50) NOT NULL,
  `user_email`           varchar(100) NOT NULL,
  `user_url`             varchar(100) NOT NULL,
  `user_registered`      datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `user_activation_key`  varchar(60) NOT NULL,
  `user_status`          int NOT NULL DEFAULT '0',
  `display_name`         varchar(250) NOT NULL,
  PRIMARY KEY (ID)
) ENGINE = MyISAM
  CHARACTER SET `utf8` COLLATE `utf8_general_ci`;

CREATE TABLE `wp_ipointvslider` (
  `id`           mediumint AUTO_INCREMENT NOT NULL,
  `option_name`  varchar(255) NOT NULL DEFAULT 'vslider_options',
  `active`       tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE = MyISAM;

