8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 31 Aug 2012 17:13:44 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:17:\"John James Jacoby\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:41;a:6:{s:4:\"data\";s:13:\"\n\t\n\t\n\t\n\t\n\t\n\t\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:58:\"Weblog Tools Collection: WordPress Theme Releases for 8/31\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:41:\"http://weblogtoolscollection.com/?p=11888\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:73:\"http://feedproxy.google.com/~r/weblogtoolscollection/UXMP/~3/tdRM1Bq18LM/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:1599:\"<p><img class=\"alignnone size-full wp-image-11889\" title=\"blackbird\" src=\"http://weblogtoolscollection.com/wp-content/uploads/2012/08/blackbird.jpg\" alt=\"\" width=\"150\" height=\"113\" /></p>\n<p><a href=\"http://wordpress.org/extend/themes/blackbird\"><strong>BlackBird</strong></a> is a uniquely designed, professional, responsive and beautiful theme with easy customization options and beautiful text styles.</p>\n<p><img class=\"alignnone size-full wp-image-11890\" title=\"mag\" src=\"http://weblogtoolscollection.com/wp-content/uploads/2012/08/mag.jpg\" alt=\"\" width=\"150\" height=\"91\" /></p>\n<p><a href=\"http://emptynestthemes.com/2012/08/25/mag-wordpress-themes/\"><strong>Mag</strong></a> is an attractive theme designed to be reminiscent of a post card, and would ideally suit a travel blog or website.</p>\n<p><img class=\"alignnone size-full wp-image-11891\" title=\"startupwp\" src=\"http://weblogtoolscollection.com/wp-content/uploads/2012/08/startupwp.jpg\" alt=\"\" width=\"150\" height=\"113\" /></p>\n<p><a href=\"http://wordpress.org/extend/themes/startupwp\"><strong>StartupWP</strong></a> is a startup theme for anyone looking to build their first website with ease and power.</p>\n<p><img class=\"alignnone size-full wp-image-11892\" title=\"wpfoodtheme\" src=\"http://weblogtoolscollection.com/wp-content/uploads/2012/08/wpfoodtheme.jpg\" alt=\"\" width=\"150\" height=\"113\" /></p>\n<p><a href=\"http://marlacucina.it/wpfoodtheme/\"><strong>WPFoodTheme</strong></a> is a green-based theme designed for recipes.</p>\n<img src=\"http://feeds.feedburner.com/~r/weblogtoolscollection/UXMP/~4/tdRM1Bq18LM\" height=\"1\" width=\"1\" />\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 31 Aug 2012 15:15:07 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"James\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:42;a:6:{s:4:\"data\";s:13:\"\n\t\n\t\n\t\n\t\n\t\n\t\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:59:\"Weblog Tools Collection: WordPress Plugin Releases for 8/29\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s